function pausebtncheck(){
    if(window.matchMedia("(max-width:720px)").matches && pausecount>0 && c%2==0 && startcount>0){

        

        playbn.style.display="inline";
        pausebn.style.display="none";
        playbn.style.width = 50 + "%";
        playbn.style.height = 50 + "%";

        document.getElementById("inputy").placeholder="Type here...";
       
        
     }
     else if((window.matchMedia("(max-width:720px)").matches && c%2!=0 && startcount>=0 && resetcount==0)){
       
        playbn.style.display="none";
       
        pausebn.style.display="inline";
        pausebn.style.width = 45 + "%";
        pausebn.style.height = 45 + "%";

        document.getElementById("inputy").placeholder="Type here...";

    }

    else{
        playbn.style.display="none";
        pausebn.style.display="none";
        document.getElementById("inputy").placeholder="Enter Space to type next word";

    }
}





function startgame(){

    missedwords=0;
    correctwords=0;
    document.getElementById("correcty").innerHTML=correctwords;
    document.getElementById("Missedy").innerHTML=missedwords;

    startgame1();
    startgame2();
}

function startgame1(){

    startcount +=1;
    if(startcount==1){
       
    timerinterval = setInterval(count,1000);
    word1interval = setInterval(startword1,speedinterval);
    word2interval = setInterval(startword2,speedinterval);
    word3interval = setInterval(startword3,speedinterval);
    word4interval = setInterval(startword4,speedinterval);
    word5interval = setInterval(startword5,speedinterval);
    userinput.value ="";

    } 

}

function startgame2(){


    if(startcount==1){

    word1position = (-Math.floor(Math.random()*130))-40;
    word2position = (-Math.floor(Math.random()*130))-20;
    word3position = (-Math.floor(Math.random()*130))-70;
    word4position = (-Math.floor(Math.random()*130))-40;
    word5position = (-Math.floor(Math.random()*100))-20;

    }

}


function pausegame(){

    pausecount+=1;
    
    

if(pausecount>0 && startcount>0 &&c%2!=0 && resetcount==0){
   

    clearInterval(word1interval);
    clearInterval(word2interval);
    clearInterval(word3interval);
    clearInterval(word4interval);
    clearInterval(word5interval);
    clearInterval(timerinterval);
    c=c+1;
    pause.innerHTML="Resume";
    pauseint = setInterval(typeprevent,10);
    function typeprevent(){
    userinput.value="";
    }
    }
 else if(startcount>0 && resetcount==0){
        pause.innerHTML="Pause";
        c=c+1;
        startcount=0;
        startgame1();
        userinput.value="";
        clearInterval(pauseint);
        
             
    }
}









function resetgame(){

    if(startcount>0){

    clearInterval(word1interval);
    clearInterval(word2interval);
    clearInterval(word3interval);
    clearInterval(word4interval);
    clearInterval(word5interval);
    clearInterval(timerinterval);
    clearInterval(pauseint)


    word1.style.visibility="hidden";
    word2.style.visibility="hidden";
    word3.style.visibility="hidden";
    word4.style.visibility="hidden";
    word5.style.visibility="hidden";

    reset=reset+1;
    startcount=0;
    pausecount=0;
    missedwords=0;
    correctwords=0;
  correct.innerHTML=correctwords;
    missed.innerHTML=missedwords;  
    
    start.innerHTML="Start";
    pause.innerHTML="Pause"
    userinput.value="";
    c=1;

 }

}









function hardgame(){

    if(hardcount%2!=0 && startcount==0){
        hard.innerHTML="Easy";
        timeinterval = 4000;
        speedinterval = 20;

        hardcount+=1;
    }
    else if (startcount==0){
        hard.innerHTML="Hard";
        hardcount+=1;
        speedinterval = 40;
        timeinterval = 8000;
    }
}


let userinputevent = userinput.addEventListener("keydown",inputcheck);

function inputcheck(event){
    if(event.key==" " || event.key=="Enter"){
        wordcount+=1;
        

        if(userinput.value.trim()==word1.innerHTML){
            word1.style.color="rgb(47, 255, 64)";
            correctwords+=1;
            userinput.value="";
            
        }
        else if(userinput.value.trim()==word2.innerHTML){
            word2.style.color="rgb(47, 255, 64)";
            correctwords+=1;
            userinput.value="";
        }
        else if(userinput.value.trim()==word3.innerHTML){
            word3.style.color="rgb(47, 255, 64)";
            correctwords+=1;
            userinput.value="";
        }
        else if(userinput.value.trim()==word4.innerHTML){
            word4.style.color="rgb(47, 255, 64)";
            correctwords+=1;
            userinput.value="";
        }
        else if(userinput.value.trim()==word5.innerHTML){
            word5.style.color="rgb(47, 255, 64)";
            correctwords+=1;
            userinput.value="";
        }
        else{
            userinput.value="";
            missedwords+=1;
        }

    }

    correct.innerHTML = correctwords;
    
}

