function count(){

if(min>=0) {

    if(sec>10){
     sec = sec - 1;
     start.innerHTML = "0"+min+":"+sec;
    }
    else if (sec>0){
     sec = sec - 1;
     start.innerHTML = "0"+min+":"+"0"+sec;
    }
    else{
        sec = 60;
        min = min-1;
    }
}

else{

    start.innerHTML = "Start";
    clearInterval(word1interval);
    clearInterval(word2interval);
    clearInterval(word3interval);
    clearInterval(word4interval);
    clearInterval(word5interval);
    clearInterval(timerinterval);
    startcount=0;
    pausecount=0;
 
    c =1;
    min =2;

}
     
}

function startword1(){
    if(word1position<0){

       
        word1.style.visibility="hidden";
        randomnum1 = Math.floor(Math.random()*nofwords);
        word1.innerHTML=words[randomnum1];
        word1.style.top = word1position + "%"
        word1position+=1;
        
    }

    else if(word1position==0){
        word1.style.visibility="visible";
        word1.style.color="white";
        word1.style.top = word1position + "%"
        word1position++;
    }

    else if(word1position<=95){
        word1.style.visibility="visible";
        word1.style.top = word1position + "%";
        word1position++;
    }

    else if(word1position==96){
        word1.style.visibility="hidden";
        
        missedwords+=1;
        missed.innerHTML = missedwords - correctwords;
        rantimeint1 = Math.floor(Math.random()*timeinterval);
        word1position++;
        let intid1 = setInterval(hide1,rantimeint1);
        word1.innerHTML=words[1000000];
        function hide1(){
            word1position = -6;
            clearInterval(intid1);
        }
            
    }
    else{
        word1.style.visibility="hidden";
        word1position++
    }



}

